function Global_GetCustomPageList(CommonPage $page, PageList $pageList)
{
    if ($page->GetPageFileName()=='home_page.php'){
        foreach ($pageList->GetPages() as $p){
            $pageList->removePage($p->getPageId());
        }
        $currentPageFilename = $page->GetPageFileName();
    
        $pageList->addGroupEx(new PageGroup('',null));
        $pageInfos = GetPageInfos();
        $expagename=['setting','inventory_location','inventory_completeness','document_attachment','user','department','office','returned_inventory'];
        foreach($pageInfos as $pageInfo) {
            $t=in_array($pageInfo['name'],$expagename) | in_array($pageInfo['group_name'],['Picklist']);
            if (!GetCurrentUserPermissionSetForDataSource($pageInfo['name'])->HasViewGrant() || $t){ continue; }
    
    
    
                $con=createConnection();
                $con->Connect();
                $r = $con->fetchAll('select value from setting where key_name=\''.$pageInfo['name'].'->description\'');
                $i=$r != null?$r[0][0]:null;
                
            $shortCaption = $pageInfo['short_caption'];
            $pageList->AddPage(new PageLink(
                $pageInfo['caption'],
                $pageInfo['filename'],
                $shortCaption,
                $currentPageFilename == $pageInfo['filename'],
                $pageInfo['add_separator'],
                '',
                isset($pageInfo['description']) ? "<a href='" . $pageInfo['filename'] . "'><img src=\"components\assets\img\\" . $pageInfo['name'] . ".png\" draggable=\"false\" width=\"200px\" height=\"200px\"/></a></br></br></br>".$i : '',
                isset($pageInfo['class_attribute']) ? $pageInfo['class_attribute'] : '',
                null,
                $pageInfo['name']
            ));
       }
    }
}

function Global_BeforeUpdateHandler($page, &$rowData, &$cancel, &$message, &$messageDisplayTime, $tableName)
{
    if ($page instanceof Page)
            {
                $mb='Modified_By';
                $md='Modified_Date';
                foreach ($rowData as $k => $v){
                        if ($k==$mb) {
                            $sql = "select `KTP/ID` from user where User_ID=" . $page->GetCurrentUserId();
                            $ret = $page->GetConnection()->fetchAll($sql)[0][0];
                            $id = $ret != null ? $ret : $page->GetCurrentUserId();
                            $rowData[$k] = $id;
                        }else if($k==$md)
                        {
                            $rowData[$k] = gmdate('Y-m-d H:i:s');
                        }
                }
            }
}

