function Global_BeforeInsertHandler($page, &$rowData, &$cancel, &$message, &$messageDisplayTime, $tableName)
{
    if ($page instanceof Page)
            {
                $mb='Created_By';
                $md='Created_Date';
                foreach ($rowData as $k => $v){
                    if ($k==$mb) {
                        $sql = "select `KTP/ID` from user where User_ID=" . $page->GetCurrentUserId();
                        $ret = $page->GetConnection()->fetchAll($sql)[0][0];
                        $id = $ret != null ? $ret : $page->GetCurrentUserId();
                        $rowData[$k] = $id;
                    }else if($k==$md)
                    {
                        $rowData[$k] = gmdate('Y-m-d H:i:s');
                    }
                }
            }
}

