}

function Global_AfterInsertHandler($page, $rowData, $tableName, &$success, &$message, &$messageDisplayTime)
{
    if (strpos($message,'Duplicate entry'))
         {
           $message='Your request canceled, this Identity (ID) exist (has been recorded).';
         }
        }
    
    
        //TODO:Remove Thumbnail
    function Global_RemThumbnail(Page $page, $rowData, $columnName=null){
        $whereClausa = '';
        $field = $page->GetDataset()->GetPrimaryKeyFieldNames();
        foreach ($field as $i => $columnName) {
            $and = $i > 0 ? ' AND ' : null;
            $whereClausa = $whereClausa . $and . " `$columnName` = \\'" . $rowData[$columnName] . "\\'";
        }
        $columnName=$columnName==null?null:' AND `Column_Name`=\\\''.$columnName.'\\\'';
        $sql = "DELETE from `thumbnail`  where `Row_ID`='".$whereClausa."' ".$columnName;
        $page->GetConnection()->ExecSQL($sql);
    }
    //TODO: Set Thumbnail
        function Global_SetThumbnail(Page $page, $rowData, $ArrayofBlobFieldName)
        {
            $tn = str_replace('`', '', $page->GetDataset()->GetName());
            $whereClausa = '';
            $field = $page->GetDataset()->GetPrimaryKeyFieldNames();
            foreach ($field as $i => $columnName) {
                $and = $i > 0 ? ' AND ' : null;
                $whereClausa = $whereClausa . $and . " `$columnName` = '" . $rowData[$columnName] . "'";
            }
            foreach ($ArrayofBlobFieldName as $blobField) {
                $filesName = strtolower($blobField) . '_edit_filename';
                if (isset($_FILES[$filesName])) {
                    $f = $_FILES[$filesName]['tmp_name'];
                    if ($f != null) {
                        $d = file_get_contents($f);
                        Global_Replace($page,$d, $tn, $blobField, $whereClausa);
        
                    }
                } else {
                    $sql='select '.$blobField.' from '.$tn. ($whereClausa != '' ? ' where '.$whereClausa : null);
                    $r = $page->GetConnection()->fetchAll($sql);
        
                    if ($r != null) {
                        $d = $r[0][0];
                        Global_Replace($page,$d, $tn, $blobField, $whereClausa);
                    }
                }
            }
        }
        
        //TODO: Save Thumbnail
        function Global_Replace(Page $page, $imageBlob, $tableName, $columnName, $rowId)
        {
            if (empty($imageBlob)){return;}
            $imageBlob=imagecreatefromstring($imageBlob);
            $w=imagesx($imageBlob);
            $h=imagesy($imageBlob);
            $m=min($w,$h);
            $recd=200;
            if ($w>$h)
            {
                $nx=($w-$h)/2;
                $im=imagecrop($imageBlob,['x'=>$nx, 'y'=>0, 'width'=>$m, 'height'=>$m]);
            }else
            {
                $ny=0;
                $im=imagecrop($imageBlob, ['x'=>0, 'y'=> $ny, 'width'=>$m, 'height'=>$m]);
        
            }
        
            $c=imagetruecolortopalette($im,false, 16000);
            $c=imagecreate($recd,$recd);
            imagecopyresampled($c,$im,0,0,0,0,$recd,$recd,$m,$m);
        
            ob_start();
            imagejpeg($c,null,100);
            $im=ob_get_contents();
            ob_end_clean();
        
            $r=addslashes($im);
            $rowId=addslashes($rowId);
            $sql = "replace INTO thumbnail (table_name, row_id, column_name, content) VALUES('{$tableName}','$rowId','$columnName', '$r')";
            $page->GetConnection()->ExecSQL($sql);
}

